mapper.select:
		select `ID`,`sale_product_id`,`rule_type`,`rule_rate`,`memo`,`validate_state`,`create_date`,`edite_date`,in_com_code from share_rule
          
		  		   and ID = #{id} 
		  		   and sale_product_id = #{saleProductId} 
		  		   and rule_type = #{ruleType} 
		  		   and rule_rate = #{ruleRate} 
		  		   and memo = #{memo} 
		  		   and validate_state = #{validateState} 
		  		   and create_date = #{createDate} 
		  		   and edite_date = #{editeDate} 
		  		
        
            
                order by ${sort} ${order}
            
			
                order by ID desc
			
        
		
			limit #{offset}, #{limit}

select count(*) from share_rule
   
  		   and ID = #{id} 
  		   and sale_product_id = #{saleProductId} 
  		   and rule_type = #{ruleType} 
  		   and rule_rate = #{ruleRate} 
  		   and memo = #{memo} 
  		   and validate_state = #{validateState} 
  		   and create_date = #{createDate} 
  		   and edite_date = #{editeDate}
